import React, { useState } from 'react';
import { ArrowUpIcon } from "@phosphor-icons/react";

const Middle = () => {
  const [selected, setSelected] = useState(null);
  const options = ['Search', 'Compare', 'Cost Estimate'];

  return (
    <div
      style={{
        padding: "1%",
        borderRadius: "12px",
        boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
        backgroundColor: "#FAFAFA",
        height: "96vh",
        width: "100%",
        textAlign: "center",
        backdropFilter: "blur(8px)",
        margin: "1vh",
      }}
    >
      Insurio
      <div
        style={{
          paddingLeft: "12px",
          paddingRight: "12px",
          backgroundColor: "#F0F0F0",
          borderRadius: "12px",
          position: "fixed",
          bottom: "6%",
          left: "50%",
          transform: "translateX(-50%)",
          width: "40%",
          border: "1px solid #E0E0E0",
        }}
      >
        <div style={{ display: "flex", marginBottom: 0, marginTop: "14px" }}>
          <input
            type="text"
            placeholder="Ask about any coverage"
            style={{
              flex: 1,
              borderRadius: "8px",
              backgroundColor: "#f0f0f0",
              marginRight: "10px",
              border: "none",
              fontSize: "16px",
              color: "#000000",
              transition: "background 0.3s",
              outline:"none",
            }}
          />
          <div
            style={{
              width: "26px",
              height: "26px",
              borderRadius: "100px",
              background: "#000000",
              color: "#A0A0A0",
              border: "none",
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <ArrowUpIcon size={20} color="#FFF" weight="bold" />
          </div>
        </div>

        {/* Clickable Options */}
        <div style={{ display: "flex", gap: "8px", justifyContent: "flex-start" }}>
          {options.map((label) => {
            const isSelected = selected === label;
            return (
              <p
                key={label}
                onClick={() => setSelected(label)}
                style={{
                  border: "1px solid",
                  borderColor: isSelected ? "#ECB26B3D" : "#E0E0E0",
                  marginBottom: "12px",
                  marginTop: "21px",
                  textAlign: "center",
                  paddingLeft: "16px",
                  paddingRight: "16px",
                  paddingTop: "10px",
                  paddingBottom: "10px",
                  color: isSelected ? "#ECB26B" : "#A0A0A0",
                  borderRadius: "10px",
                  backgroundColor: isSelected ? "rgba(236, 178, 107, 0.24)" : "#FAFAFA",
                  display: "flex",
                  alignItems: "center",
                  cursor: "pointer",
                  transition: "background 0.3s, border-color 0.3s, color 0.3s",
                  userSelect: "none",
                }}
              >
                {label}
              </p>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Middle;
